import appendStyle from './appendStyle.js';
import STYLES from './styles.js';

function applyParams(target, args) {
    let template = '';
    let params = [];
    for (let i of args) {
        if (typeof i === 'string') {
            template += '%s';
        }
        else {
            template += '%o';
        }
        params.push(i);
    }
    if (STYLES.BASESTYLE && !target._template) {
        appendStyle(target, ';');
    }
    target._template += template;
    target._params.push(...params);
}

export { applyParams as default };
